				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}
							$$ {\tilde{S}}_j={\displaystyle \sum_{k=1,2}}{\tilde{S}}_{k,j}={\displaystyle \sum_{k=1,2}} \ln \operatorname{}{\tilde{Z}}_{k,j}={\displaystyle \sum_{k=1,2}} \ln \frac{N_{k,j}!}{{\displaystyle {\prod}_{i=1,\dots, 16}}{\tilde{N}}_{i,k,j}!} $$
						\end{document}

